import React, { useEffect, useState } from 'react'
import { Link, useNavigate, useParams } from 'react-router-dom';
import axios from "axios";
import "../addmedicine/add.css";
import toast from 'react-hot-toast';

const Edit = () => {

 const medicines = {
    medicine : "",
    desc : "",
    quantity : "",
    expiry : ""
 }

 const {id} = useParams();
 const navigate = useNavigate();
 const [medicine, setMedicine] = useState(medicines);

 const inputChangeHandler = (e) =>{
    const {name, value} = e.target;
    setMedicine({...medicine, [name]:value});
    console.log(medicine);
 }

 useEffect(()=>{
    axios.get(`http://localhost:8000/api/getone/${id}`)
    .then((response)=>{
        setMedicine(response.data)
    })
    .catch((error)=>{
        console.log(error);
    })
 },[id])


 const submitForm = async(e)=>{
    e.preventDefault();
    await axios.put(`http://localhost:8000/api/update/${id}`, medicine)
    .then((response)=>{
       toast.success(response.data.msg, {position:"top-right"})
       navigate("/")
    })
    .catch(error => console.log(error))
 }

  return (
    <div className='addMedicine'>
        <Link to={"/"}>Back</Link>
        <h3>Update Medicine</h3>
        <form className='addMedicineForm' onSubmit={submitForm}>
            <div className="inputGroup">
                <label htmlFor="medicine">Medicine Name</label>
                <input type="text" value={medicine.medicine} onChange={inputChangeHandler} id="medicine" name="medicine" autoComplete='off' placeholder='First name'  disabled/>
            </div>
            <div className="inputGroup">
                <label htmlFor="desc">Medicine Description</label>
                <input type="text" value={medicine.desc} onChange={inputChangeHandler} id="desc" name="desc" autoComplete='off' placeholder='First name' disabled/>
            </div>
            <div className="inputGroup">
                <label htmlFor="quantity">Medicine Quantity</label>
                <input type="number" value={medicine.quantity} onChange={inputChangeHandler} id="quantity" name="quantity" autoComplete='off' placeholder='First name' />
            </div>
            <div className="inputGroup">
                <label htmlFor="expiry">Expiry Date</label>
                <input type="text" value={medicine.expiry} onChange={inputChangeHandler} id="expiry" name="expiry" autoComplete='off' placeholder='First name' disabled/>
            </div>
            <div className="inputGroup">
                <button type="submit">UPDATE MEDICINE</button>
            </div>
        </form>
    </div>
  )
}

export default Edit
