import React, { useEffect, useState } from 'react'
import axios from "axios";
import toast from "react-hot-toast";
import "./medicine.css";
import { Link } from 'react-router-dom'

const Medicine = () => {

  const [medicines, setMedicines] = useState([]);

  useEffect(()=>{

    const fetchData = async()=>{
        const response = await axios.get("http://localhost:8000/api/getall");
        setMedicines(response.data);
    }

    fetchData();

  },[])

  const deleteMedicine = async(medicineId) =>{
      await axios.delete(`http://localhost:8000/api/delete/${medicineId}`)
      .then((respones)=>{
        setMedicines((prevMedicine)=> prevMedicine.filter((medicine)=> medicine._id !== medicineId))
        toast.success(respones.data.msg, {position: 'top-right'})
      })
      .catch((error) =>{
        console.log(error);
      })
  }

  const formatDate = (dateString) => {
    const options = { day: '2-digit', month: '2-digit', year: 'numeric' };
    const formattedDate = new Date(dateString).toLocaleDateString(undefined, options);

    // Convert the date to DD-MM-YYYY format
    const [day, month, year] = formattedDate.split('/');
    return `${month}-${day}-${year}`;
};

  return (
    <div className='medicineTable'>
        <Link to={"/add"} className='addButton'>Add Medicine</Link>
        <table border={1} cellPadding={10} cellSpacing={0}>
            <thead>
                <tr>
                    <th>S.No.</th>
                    <th>Medicine</th>
                    <th>Description</th>
                    <th>Quantity</th>
                    <th>Expiry Date</th>

                    <th>Actions</th>
                </tr>
            </thead>
            <tbody>
                {
                    medicines.map((medicine, index)=>{
                        return(
                        <tr key={medicine._id}>
                            <td>{index + 1}</td>
                            <td>{medicine.medicine}</td>
                            <td>{medicine.desc}</td>
                            <td>{medicine.quantity}</td>
                            {/* <td>{medicine.expiry}</td> */}
                            <td>{formatDate(medicine.expiry)}</td>
                            <td className='actionButtons'>
                                <button onClick={()=> deleteMedicine(medicine._id)}><i className="fa-solid fa-trash"></i></button>
                                <Link to={`/edit/`+medicine._id}><i className="fa-solid fa-pen-to-square"></i></Link>
                            </td>
                        </tr>
                        )
                    })
                }
                
            </tbody>
        </table>
    </div>
  )
}

export default Medicine;
