import React, { useState } from 'react'
import { Link, useNavigate } from 'react-router-dom';
import axios from "axios";
import "./add.css";
import toast from 'react-hot-toast';

const Add = () => {

  const medicines = {
    medicine : "",
    desc : "",
    quantity : "",
    expiry : ""
  }

  const [medicine, setMedicine] = useState(medicines);
  const navigate = useNavigate();

  const inputHandler = (e) =>{
      const {name, value} = e.target;
      setMedicine({...medicine, [name]:value});
  }

  const submitForm = async(e) =>{
    e.preventDefault();
    await axios.post("http://localhost:8000/api/create", medicine)
    .then((response)=>{
       toast.success(response.data.msg, {position:"top-right"})
       navigate("/")
    })
    .catch(error => console.log(error))
  }


  return (
    <div className='addMedicine'>
        <Link to={"/"}>Back</Link>
        <h3>Add new medicine</h3>
        <form className='addMedicineForm' onSubmit={submitForm}>
            <div className="inputGroup">
                <label htmlFor="medicine">Medicine Name</label>
                <input type="text" onChange={inputHandler} id="medicine" name="medicine" autoComplete='off' placeholder='Medicine Name' />
            </div>
            <div className="inputGroup">
                <label htmlFor="desc">Medicine Description</label>
                <input type="text" onChange={inputHandler} id="desc" name="desc" autoComplete='off' placeholder='Medicine Description' />
            </div>
            <div className="inputGroup">
                <label htmlFor="quantity">Medicine Quantity</label>
                <input type="number" onChange={inputHandler} id="quantity" name="quantity" autoComplete='off' placeholder='Quantity' />
            </div>
            <div className="inputGroup">
                <label htmlFor="expiry">Expiry Date</label>
                <input type="date" onChange={inputHandler} id="expiry" name="expiry" autoComplete='off' placeholder='Expiry Date' />
            </div>
            <div className="inputGroup">
                <button type="submit">ADD MEDICINE</button>
            </div>
        </form>
    </div>
  )
}

export default Add
