import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import toast from 'react-hot-toast';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS
// import './add.css';

const Add = () => {
  const medicines = {
    medicine: '',
    desc: '',
    quantity: '',
    expiry: '',
  };

  const [medicine, setMedicine] = useState(medicines);
  const navigate = useNavigate();

  const inputHandler = (e) => {
    const { name, value } = e.target;
    setMedicine({ ...medicine, [name]: value });
  };

  const submitForm = async (e) => {
    e.preventDefault();
    await axios
      .post('http://localhost:8000/api/create', medicine)
      .then((response) => {
        toast.success(response.data.msg, { position: 'top-right' });
        navigate('/');
      })
      .catch((error) => console.log(error));
  };

  return (
    <div className='container mt-5'>
      <Link to={'/'} className='btn btn-secondary mb-3'>
        Back
      </Link>
      <div className='addMedicine'>
        <h3>Add new medicine</h3>
        <form className='addMedicineForm' onSubmit={submitForm}>
          <div className='mb-3'>
            <label htmlFor='medicine' className='form-label'>
              Medicine Name
            </label>
            <input
              type='text'
              onChange={inputHandler}
              id='medicine'
              name='medicine'
              className='form-control'
              autoComplete='off'
              placeholder='Medicine Name'
            />
          </div>
          <div className='mb-3'>
            <label htmlFor='desc' className='form-label'>
              Medicine Description
            </label>
            <input
              type='text'
              onChange={inputHandler}
              id='desc'
              name='desc'
              className='form-control'
              autoComplete='off'
              placeholder='Medicine Description'
            />
          </div>
          <div className='mb-3'>
            <label htmlFor='quantity' className='form-label'>
              Medicine Quantity
            </label>
            <input
              type='number'
              onChange={inputHandler}
              id='quantity'
              name='quantity'
              className='form-control'
              autoComplete='off'
              placeholder='Quantity'
            />
          </div>
          <div className='mb-3'>
            <label htmlFor='expiry' className='form-label'>
              Expiry Date
            </label>
            <input
              type='date'
              onChange={inputHandler}
              id='expiry'
              name='expiry'
              className='form-control'
              autoComplete='off'
              placeholder='Expiry Date'
            />
          </div>
          <div className='mb-3'>
            <button type='submit' className='btn btn-primary'>
              ADD MEDICINE
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Add;
